mapper.select:
select * from factory_c
where 1=1
 and STATE=#{state}

mapper.update:
update factory_c
FULL_NAME=#{fullName, jdbcType=VARCHAR}, FACTORY_NAME=#{factoryName, jdbcType=VARCHAR}, CONTACTS=#{contacts, jdbcType=VARCHAR}, PHONE=#{phone, jdbcType=VARCHAR}, MOBILE=#{mobile, jdbcType=VARCHAR}, FAX=#{fax, jdbcType=VARCHAR}, CNOTE=#{cnote, jdbcType=VARCHAR}, INSPECTOR=#{inspector, jdbcType=VARCHAR}, ORDER_NO=#{orderNo, jdbcType=INTEGER}, STATE=#{state, jdbcType=VARCHAR},
where FACTORY_ID=#{id}

mapper.delete:
delete from factory_c
where FACTORY_ID in

	#{id}

mapper.update:
update factory_c set state=#{state}
where FACTORY_ID in

	#{id}
