mapper.update:
update packing_list_c
SELLER=#{seller}, BUYER=#{buyer}, INVOICE_NO=#{invoiceNo}, INVOICE_DATE=#{invoiceDate}, MARKS=#{marks}, DESCRIPTIONS=#{descriptions}, EXPORT_IDS=#{exportIds}, EXPORT_NOS=#{exportNos}, STATE=#{state},
where packing_list_id=#{id}

mapper.delete:
delete from packing_list_c
where packing_list_id in

	#{id}
